import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Importa axios

function App() {
  const [users, setUsers] = useState([]); // Usa useState

  useEffect(() => {
    // Hacemos la petición al backend cuando el componente carga
    axios.get('http://localhost:5000/api/users')
      .then(response => {
        setUsers(response.data);  // Guardamos la lista de usuarios en el estado
      })
      .catch(error => {
        console.error('Hubo un error al obtener los usuarios:', error);
      });
  }, []);  // El array vacío significa que esto se ejecuta solo una vez, al montar el componente.

  return (
    <div className="App">
      <header className="App-header">
        <h1>Lista de Usuarios</h1>
        <ul>
          {users.map(user => (
            <li key={user.id}>{user.name}</li>
          ))}
        </ul>
      </header>
    </div>
  );
}

export default App;
